          const cards = response.data.cards;
          setCardsTarot(cards);
          setPathCard({ url: response.data.imagesUrl, cardBack: response.data.imageBackCard });
        }
      })
      .catch(error => console.log(error))
  }, [])


  const GameInit = () => {
    setCardsTarot(shuffle(filterCard));
